//Javascript innerHTML內嵌之i18n
function i18n(key) {
  const language = localStorage.getItem('language');
  const keySplit = key.split('.');

  if (!lang[language]) {
    language = 'en_US';
  }

  return keySplit.length > 1
    ? keySplit.reduce((e, current) => {
        return lang[language][e][current];
      })
    : lang[language][key];
}

//原生HTML之i18n
function getDataI18n() {
  const elements = document.querySelectorAll('[data-i18n]');
  elements.forEach((element, i) => {
    element.textContent = i18n(element.getAttribute('data-i18n'));
    if (element.tagName === 'INPUT') {
      element.setAttribute(
        'placeholder',
        i18n(element.getAttribute('data-i18n')),
      );
    }
  });
}
$(document).ready(function () {
  getDataI18n();
});
